// File: client/src/components/Nav.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Importamos Link para la navegación

const Nav = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const menuItems = [
    { icon: '/src/icons/casa (1).png', text: 'Home', link: '/home' },
    { icon: '/assets/icons/usuario.png', text: 'Perfil', link: '/perfil' },
    { icon: '/assets/icons/usuarios-de-grupo.png', text: 'Equipo', link: '/equipo' },
    { icon: '/assets/icons/rebote.png', text: 'Entrenamiento', link: '/entrenamiento' },
    { icon: '/assets/icons/bolsa-para-la-escuela.png', text: 'Ejercicios', link: '/ejercicios' },
    { icon: '/assets/icons/jugador-de-baloncesto.png', text: 'RoadMap', link: '/roadmap' },
    { icon: '/assets/icons/fecha-del-calendario.png', text: 'Planificación', link: '/planificacion' },
    { icon: '/assets/icons/ejercicio.png', text: 'Rendimiento', link: '/rendimiento' },
    { icon: '/assets/icons/deporte.png', text: 'Psicología', link: '/psicologia' },
    { icon: '/assets/icons/estadisticas.png', text: 'Estadísticas', link: '/estadisticas' },
    { icon: '/assets/icons/biblioteca-en-linea.png', text: 'Cursos', link: '/cursos' },
    { icon: '/assets/icons/engranaje.png', text: 'Configuración', link: '/configuracion' },
  ];

  return (
    <div className={`sidebar ${isMenuOpen ? 'open' : ''}`}>
      <button className="toggle-btn" onClick={toggleMenu}>
        <img src="/assets/icons/logoAcademiaFinal.jpg" alt="Logo" />
      </button>
      {isMenuOpen && (
        <ul className="menu">
          {menuItems.map((item, index) => (
            <li key={index} className="menu-item">
              <Link to={item.link} className="menu-link">
                <img className="icon" src={item.icon} alt={`Ícono de ${item.text}`} />
                <span className="text">{item.text}</span>
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Nav;
