import React, { useEffect } from 'react';
import Nav from '../components/Nav';
import '../styles/Nav.css'; // Ruta corregida
import '../styles/Home.css'; // Ruta corregida

const Home = () => {
  useEffect(() => {
    // Agrega la clase al body cuando el componente se monta
    document.body.classList.add('home-body');

    // Limpia la clase cuando el componente se desmonta
    return () => {
      document.body.classList.remove('home-body');
    };
  }, []);

  return (
    <div style={{ display: 'flex' }}>
      <Nav />
      <main style={{ marginLeft: '120px', padding: '16px', flex: 1 }}>
        <h1>Bienvenido a la Academia</h1>
        <p>Contenido principal aqu√≠.</p>
      </main>
    </div>
  );
};

export default Home;
