// filepath: /client/src/components/PrivateRoute.jsx
import React from 'react';
import { Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';  // Asegúrate de que esto está correctamente


const PrivateRoute = ({ children }) => {
  const token = localStorage.getItem('token');

  if (!token) {
    return <Navigate to="/login" replace />;
  }

  try {
    const decoded = jwtDecode(token);

    // Verifica si el token ha expirado
    if (decoded.exp * 1000 < Date.now()) {
      localStorage.removeItem('token'); // Limpia token caducado
      return <Navigate to="/login" replace />;
    }

    return children; // Usuario válido
  } catch (error) {
    console.error('Token inválido:', error);
    localStorage.removeItem('token');
    return <Navigate to="/login" replace />;
  }
};

export default PrivateRoute;
