// File: client/src/pages/Home.js

import React, { useState, useEffect } from 'react';
import Nav from '../components/Nav'; // Importa el componente de navegación
import '../styles/Nav.css'; // Importa los estilos para la barra de navegación
import '../styles/Home.css'; // Importa los estilos específicos para la página principal
import NoticiasCarrusel from "../components/NoticiasCarrusel"; // Importa el componente del carrusel de noticias
import "slick-carousel/slick/slick.css"; // Importa los estilos para el carrusel
import "slick-carousel/slick/slick-theme.css"; // Importa los estilos del tema para el carrusel

const Home = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(true); // Inicializamos como `true` por defecto (barra abierta)

  const handleMenuToggle = (isOpen) => {
    setIsMenuOpen(isOpen); // Cambia el estado de la barra de navegación
  };

  useEffect(() => {
    // Agrega la clase al body cuando el componente se monta
    document.body.classList.add('home-body');

    // Limpia la clase cuando el componente se desmonta
    return () => {
      document.body.classList.remove('home-body');
    };
  }, []); // El efecto solo se ejecuta una vez al montar el componente

  return (
    <div style={{ display: 'flex' }}>
      <Nav onToggle={handleMenuToggle} />  {/* Pasamos la función para notificar sobre la expansión del menú */}
      <main style={{ marginLeft: isMenuOpen ? '250px' : '80px', padding: '16px', flex: 1 }}>
        <h1>Falcons Edge Basketball Academy</h1> {/* Título de la página */}

        <div className="content-principal">
          {/* Sección de noticias */}
          <div className="content-noticias">
            <h2>Welcome to the Academy!</h2>
            <NoticiasCarrusel /> {/* Componente carrusel con las noticias */}
          </div>

          {/* Sección de perfil */}
          <div className="content-perfil">
            <h2>Join us for training, games, and fun!</h2>
            <p>Espacio para perfil.</p> {/* Información del perfil */}
          </div>
        </div>

        {/* Sección de acceso rápido */}
        <div className="content-acceso-rapido">
          <h2>Contact us for more information!</h2>
          <p>Espacio para acceso rápido.</p> {/* Información adicional o enlaces rápidos */}
        </div>
      </main>
    </div>
  );
};

export default Home;
